args.query:
SELECT symbol, proper_title, title, un_body, publication_date, effective_year FROM latest_versions 
WHERE proper_title ILIKE '%Sustainable Development Goals%' OR title ILIKE '%Sustainable Development Goals%' OR title ILIKE '%SDG%' OR proper_title ILIKE '%SDG%'
ORDER BY effective_year DESC;

